<!DOCTYPE html>
<html>

<head>
    <style>
        .dot-safe {
            height: 20px;
            width: 20px;
            background-color: #46f33a;
            border-radius: 50%;
            display: inline-block;
        }

        .dot-normal {
            height: 25px;
            width: 25px;
            background-color: #46f33a;
            border-radius: 50%;
            display: inline-block;
        }

        .dot-warning {
            height: 30px;
            width: 30px;
            background-color: #ffa500;
            border-radius: 50%;
            display: inline-block;
        }

        .dot-danger {
            height: 35px;
            width: 35px;
            background-color: #ff0000;
            border-radius: 50%;
            display: inline-block;
        }
    </style>
</head>

<body style="background-color: #F0FFF0;">
    <br><br><br>
    <div id="chartContainer" style="height: 250px; max-width: 520px; margin: 0px auto;"></div>
    <br>
    <h2 style="text-align: center">Monitoring Suhu Real Time</h2>

    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC" crossorigin="anonymous">
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css">
    <script src="https://canvasjs.com/assets/script/canvasjs.min.js"></script>
    <script src="https://code.jquery.com/jquery-3.5.1.js" integrity="sha256-QWo7LDvxbWT2tbbQ97B53yJnYU3WhH/C8ycbRAkjPDc=" crossorigin="anonymous"></script>

    <script>
        function beep() {
            var snd = new Audio("data:audio/wav;base64,");
            snd.play();
        }

        window.onload = function() {
            var dps = [];
            var dataLength = 10;
            var updateInterval = 1000;
            var xVal = 0;
            var yVal = 0;

            var chart = new CanvasJS.Chart("chartContainer", {
                title: {
                    text: "Grafik Suhu Realtime"
                },
                data: [{
                    type: "line",
                    dataPoints: dps
                }]
            });

            var updateChart = function(count) {
                $.getJSON("http://localhost/monitor/getdata.php", function(data) {
                    var suhu = data.suhu

                    if (suhu < 29) {
                        document.getElementById("indikator").textContent = "Suhu : " + suhu.toString() + " (AMAN)";
                        document.getElementById("indikator").className = "alert alert-success";
                        document.getElementById("dot").className = "dot-safe";
                    } else if (suhu >= 29 && suhu < 30) {
                        document.getElementById("indikator").textContent = "Suhu : " + suhu.toString() + " (NORMAL)";
                        document.getElementById("indikator").className = "alert alert-success";
                        document.getElementById("dot").className = "dot-normal";
                    } else if (suhu >= 30 && suhu < 31) {
                        document.getElementById("indikator").textContent = "Suhu : " + suhu.toString() + " (PANAS)";
                        document.getElementById("indikator").className = "alert alert-warning";
                        document.getElementById("dot").className = "dot-warning";
                        beep();
                    } else if (suhu >= 31) {
                        document.getElementById("indikator").textContent = "Suhu : " + suhu.toString() + " (SANGAT PANAS)";
                        document.getElementById("indikator").className = "alert alert-danger";
                        document.getElementById("dot").className = "dot-danger";
                        beep();
                        beep();
                        beep();
                    }

                    console.log(suhu)
                    yVal = suhu
                    count = count || 1;

                    for (var j = 0; j < count; j++) {
                        dps.push({
                            x: xVal,
                            y: yVal
                        });
                        xVal++;
                    }

                    if (dps.length > dataLength) {
                        dps.shift();
                    }

                });
                chart.render();

            };

            updateChart(dataLength);

            setInterval(function() {
                updateChart()
            }, updateInterval);

        }
    </script>

    <center>
        <div class="w-25 p-3">
            <div class="alert alert-success" role="alert" id="indikator">
                <b>KETERANGAN</b>
            </div>
            <span class="dot-safe" id="dot"></span>
        </div>
    </center>

</body>

</html>